package com.intermancer.gaiaf.core.experiment;

import java.util.Random;

import com.intermancer.gaiaf.core.organism.Chromosome;
import com.intermancer.gaiaf.core.organism.Gene;

/**
 * Utility class for generating random chromosomes to support mutation activities.
 */
public class ChromosomeGenerator {
    
    private static final Random random = new Random();
    
    /**
     * Returns a randomly generated Chromosome created with between 3 and 6 
     * random Genes generated by GeneGenerator.
     * 
     * @return A randomly generated Chromosome
     */
    public static Chromosome getRandomChromosome() {
        Chromosome chromosome = new Chromosome();
        
        // Generate between 3 and 6 genes
        int numberOfGenes = random.nextInt(4) + 3; // 3 to 6
        
        for (int i = 0; i < numberOfGenes; i++) {
            Gene gene = GeneGenerator.getRandomGene();
            chromosome.getGenes().add(gene);
        }
        
        return chromosome;
    }
}