package com.intermancer.gaiaf.core.experiment;

/**
 * Represents a single mutation possibility on a single genetic element.
 * MutationCommands are generated by classes that implement the Mutational interface.
 */
public interface MutationCommand {
    /**
     * Performs the mutation.
     */
    void execute();
    
    /**
     * Gets a description of what this mutation will do.
     * 
     * @return Description of the mutation
     */
    default String getDescription() {
        return this.getClass().getSimpleName();
    }
}